package print_method;

public class Ex1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 형식 지정 문자를 사용한 printf() 매서드
		// %d : 10진수 1개를 표현
		// %xd : x자리수 만큼의 자리를 차지
		// %0xd : x자리수 만큼의 자리를 차지하고 빈자리를 0으로 채움
		// ex) printf("%02d", 2); -> 02
		System.out.printf("%d\n", 1);
		// %f : 실수 표현
		// %c : 문자 표현
		// %s : 문자열 표현
	}

}
